use super::*;
use crate::Pallet as Verifier;
pub use frame_benchmarking::{account, benchmarks};
use frame_system::RawOrigin;
use fuso_support::constants::*;
use fuso_support::external_chain::XToken;
use sp_runtime::traits::CheckedAdd;
use sp_runtime::traits::StaticLookup;
use sp_runtime::DispatchResult;

pub type BalanceOf<T> = <T as pallet_balances::Config>::Balance;

const SEED: u32 = 0;

pub const MILLICENTS: u128 = 10_000_000_000_000;
pub const CENTS: u128 = 1_000 * MILLICENTS;
// assume this is worth about a cent.
pub const DOLLARS: u128 = 100 * CENTS;

benchmarks! {
    where_clause {
        where
        TokenId<T>: Copy + From<u32> + Into<u32>,
        Balance<T>: Copy + From<u128> + Into<u128>,
        BalanceOf<T>: From<u128> + Into<u128>,
        T::BlockNumber: Into<u32> + From<u32>,
        T: pallet_balances::Config,
        T: pallet_fuso_token::Config,
    }

    register {
        frame_system::Pallet::<T>::set_block_number(3000.into());
        let ben: T::AccountId = account("Ben", 0, SEED);
    } :_(RawOrigin::Signed(ben), b"cool".to_vec())

    evict {
        frame_system::Pallet::<T>::set_block_number(3000.into());
        let ben: T::AccountId = account("Ben", 0, SEED);
        Verifier::<T>::register(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            b"cool".to_vec()
        )?;
        let dominator = T::Lookup::unlookup(ben.clone());
        Verifier::<T>::launch(<T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Root), dominator.clone())?;
        Verifier::<T>::stake(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            dominator.clone(),
            (100_000 * DOLLARS).into()
        )?;
    }: evict(RawOrigin::Root, dominator)

    launch {
        frame_system::Pallet::<T>::set_block_number(3000.into());
        let ben: T::AccountId = account("Ben", 0, SEED);
        Verifier::<T>::register(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            b"cool".to_vec()
        )?;
        let dominator = T::Lookup::unlookup(ben.clone());
    }: _(RawOrigin::Root, dominator)

    stake {
        frame_system::Pallet::<T>::set_block_number(3000.into());
        let ben: T::AccountId = account("Ben", 0, SEED);
        Verifier::<T>::register(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            b"cool".to_vec()
        )?;
        let dominator = T::Lookup::unlookup(ben.clone());
        Verifier::<T>::launch(<T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Root), dominator.clone())?;
    } :_(RawOrigin::Signed(ben), dominator, (100_000 * DOLLARS).into())

    unstake {
        frame_system::Pallet::<T>::set_block_number(3000.into());
        let ben: T::AccountId = account("Ben", 0, SEED);
        let _ = Verifier::<T>::register(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            b"cool".to_vec()
        )?;
        let dominator = T::Lookup::unlookup(ben.clone());
        Verifier::<T>::launch(<T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Root), dominator.clone())?;
        Verifier::<T>::stake(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            dominator.clone(),
            (100_000 * DOLLARS).into()
        )?;
    } :_(RawOrigin::Signed(ben), dominator, (50_000 * DOLLARS).into())

    claim_shares {
        frame_system::Pallet::<T>::set_block_number(3000.into());
        let ben: T::AccountId = account("Ben", 0, SEED);
        let lance: T::AccountId = account("Lance", 0, SEED);
        let _ = Verifier::<T>::register(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            b"cool".to_vec()
        )?;
        let dominator = T::Lookup::unlookup(ben.clone());
        Verifier::<T>::launch(<T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Root), dominator.clone())?;
        Verifier::<T>::stake(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            dominator.clone(),
            (100_000 * DOLLARS).into()
        )?;
        Verifier::<T>::stake(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(lance.clone())),
            dominator.clone(),
            (50_000 * DOLLARS).into()
        )?;
    } :_(RawOrigin::Signed(lance), dominator)

    authorize {
        frame_system::Pallet::<T>::set_block_number(3000.into());
        let ben: T::AccountId = account("Ben", 0, SEED);
        let lance: T::AccountId = account("Lance", 0, SEED);
        Verifier::<T>::register(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            b"cool".to_vec()
        )?;
        let dominator = T::Lookup::unlookup(ben.clone());
        pallet_fuso_token::Pallet::<T>::issue(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(lance.clone())),
            XToken::NEP141(
                br#"USDT"#.to_vec(),
                br#"usdt.testnet"#.to_vec(),
                0u32.into(),
                true,
                6
            )
        )?;
        pallet_fuso_token::Pallet::<T>::do_mint(
            1u32.into(),
            &lance,
            (100_000 * DOLLARS).into(),
            None
        )?;
        Verifier::<T>::launch(<T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Root), dominator.clone())?;
        Verifier::<T>::stake(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            dominator.clone(),
            (100_000 * DOLLARS).into()
        )?;
    } :_(RawOrigin::Signed(lance), dominator, 1.into(), (50_000 * DOLLARS).into())

    revoke {
        frame_system::Pallet::<T>::set_block_number(3000.into());
        let ben: T::AccountId = account("Ben", 0, SEED);
        let lance: T::AccountId = account("Lance", 0, SEED);
        Verifier::<T>::register(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            b"cool".to_vec()
        )?;
        let dominator = T::Lookup::unlookup(ben.clone());
        Verifier::<T>::launch(<T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Root), dominator.clone())?;
        pallet_fuso_token::Pallet::<T>::issue(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(lance.clone())),
               XToken::NEP141(
                br#"USDT"#.to_vec(),
                br#"usdt.testnet"#.to_vec(),
                0u32.into(),
                true,
                6
            )
        )?;
        pallet_fuso_token::Pallet::<T>::do_mint(
            1u32.into(),
            &lance,
            (100_000 * DOLLARS).into(),
            None
        )?;
        Verifier::<T>::stake(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(ben.clone())),
            dominator.clone(),
            (100_000 * DOLLARS).into()
        )?;
        Verifier::<T>::authorize(
            <T as frame_system::Config>::RuntimeOrigin::from(RawOrigin::Signed(lance.clone())),
            dominator.clone(),
            1u32.into(),
            (60_000 * DOLLARS).into(),
        )?;
       Reserves::<T>::remove(
            &(
                RESERVE_FOR_AUTHORIZING_STASH,
                lance.clone(),
                1u32.into(),
            ),
            ben.clone(),
       );
       Reserves::<T>::try_mutate(
            &(
                RESERVE_FOR_AUTHORIZING,
                lance.clone(),
                1u32.into(),
            ),
            ben.clone(),
            |ov| -> DispatchResult {
                Ok(*ov = ov.checked_add(&(60_000 * DOLLARS).into()).ok_or(Error::<T>::Overflow)?)
            },
        )?;
       Receipts::<T>::remove(ben.clone(), lance.clone());
    } :_(RawOrigin::Signed(lance), dominator, 1u32.into(), (50_000 * DOLLARS).into())
}
